export default {
  Button: {
    baseStyle: ({ theme }: any) => ({
      _disabled: {
        opacity: 1,
        cursor: "not-allowed",
        boxShadow: "none",
        border: "4px solid",
        borderColor: theme.colors.black,
        bgColor: theme.colors.gray,
        color: theme.colors.black,
        _hover: {
          borderColor: theme.colors.black,
          bgColor: theme.colors.gray,
          color: theme.colors.black,
        },
      },
      _hover: {
        _disabled: {
          borderColor: theme.colors.black,
          bgColor: theme.colors.gray,
          color: theme.colors.black,
        },
      },
    }),
    sizes: {
      md: {
        border: "none",
        boxSizing: "border-box",
        borderColor: "black",
        padding: "12px 24px",
        fontSize: "lg",
      },
    },
    variants: {
      outline: ({ theme }: any) => ({
        border: "4px solid",
        borderColor: theme.colors.black,
        color: theme.colors.black,
        bg: theme.colors.white,
        _hover: {
          bg: theme.colors.lightGray,
          color: theme.colors.black,
        },
        _active: {
          bg: theme.colors.gray,
          color: theme.colors.black,
          borderColor: theme.colors.black,
        },
      }),
      solid: ({ theme }: any) => ({
        border: "4px solid",
        borderColor: theme.colors.black,
        color: theme.colors.white,
        bg: theme.colors.black,
        _hover: {
          bg: theme.colors.darkGray,
          color: theme.colors.white,
        },
        _active: {
          bg: theme.colors.gray,
          color: theme.colors.white,
          borderColor: theme.colors.black,
        },
      }),
      ghost: {
        _hover: {
          bg: "transparent",
        },
        _active: {
          bg: "transparent",
        },
        _focus: {
          bg: "transparent",
        },
      },
    },
  },
};
